mapper.select:
SELECT * FROM (
	SELECT rownum AS rnum, review.review_no, review.cor_no, cor.cor_name, review.review_title, review.review_tag, review.review_recommended, review.review_hit, review.review_written_date 
	FROM review, cor
	WHERE review.cor_no = cor.cor_no AND review_tag = #{tag }
	ORDER BY review_no DESC
)
WHERE rnum BETWEEN #{startNo } AND #{endNo }

SELECT * FROM (
	SELECT rownum AS rnum, review.review_no, review.cor_no, cor.cor_name, review.review_title, review.review_tag, review.review_recommended, review.review_hit, review.review_written_date 
	FROM review, cor
	WHERE review.cor_no = cor.cor_no AND cor.cor_name = #{tag }
	ORDER BY review_no DESC
)
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE review
SET review_hit = review_hit + 1
WHERE review_no = #{reviewno }
